INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF1','FF 1 : Hira Faneva Faha-30 Taonan''ny Fjkm');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF1', 'Mitsangàna ianao ry mino\nFa ilain''ny taninao\nNy finoana ifikiro,\nMivonona ianao !\nNy ady natrehanao tsy mora\nTena sarotra tokoa\nNefa toy ''lay tao Edoma\n''Lay Mahery momba anao.\n\nFiv:\nMbola ho avy ny maraina\n(''lay maraina)\n''Zay mitondra hery vao.\nRy mpiambina miarena\nKa ingao ny zovinao.\nAmbarao fa ny aizina\nIndro efa lasa re\nEfa tonga any maraina\nHisy hazavana be', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF1', 'Raha sendra ady mafy\nMila handreraka ny fo\nDia tsarovy fa mihafy\nVao hahita soa\nRaha tafita ianao\nHisy hazavana be\nNy haizin-kitroka ho lao\nTapi-dàlana haleha\nFiv:\nMbola ho avy ny maraina\n(''lay maraina)\n''Zay mitondra hery vao.\nRy mpiambina miarena\nKa ingao ny zovinao.\nAmbarao fa ny aizina\nIndro efa lasa re\nEfa tonga any maraina\nHisy hazavana be', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF1', 'Fahafahana doria\nTsisy tambiny aminao\nNo omen''ny Tompo tia\nRaha tahiana ianao\nHo fanilo mandra-maty\nHo mpitantana ny dia\nHomba anao doria doria\nFiv:\nMbola ho avy ny maraina\n(''lay maraina)\n''Zay mitondra hery vao.\nRy mpiambina miarena\nKa ingao ny zovinao.\nAmbarao fa ny aizina\nIndro efa lasa re\nEfa tonga any maraina\nHisy hazavana be', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF2','FF 2 : He! Manolo Tena');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF2', 'He, manolo-tena ho Anao\nFa mandre ny feo sy antsonao\n''Zato tenako izay mania\nKa raiso Tompo, raiso ry Jesoa tia\nFiv:\nHanoa ny sitrakao, hanao ''zay tianao\nKa ho mpanompo vonona sy sahy koa\nHitory hatrany ny fitiavanao\nAmbaram-pialam-pofon''aiko Jesoa', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF2', 'Eny, Tompo, tsy mba mendrika aho\nFeno loto ao am-poko ao\nNefa re ilay fitiavanao\nNo inoako handray ahy ho mpanomponao\nFiv:\nHanoa ny sitrakao, hanao ''zay tianao\nKa ho mpanompo vonona sy sahy koa\nHitory hatrany ny fitiavanao\nAmbaram-pialam-pofon''aiko Jesoa', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF2', 'Eo anilanao no fifaliako\nHo Anao mandrakizay no tiako\nNy ho vavolombelonao Jesoa\nNo mamiko sy tena iriko tokoa\nFiv:\nHanoa ny sitrakao, hanao ''zay tianao\nKa ho mpanompo vonona sy sahy koa\nHitory hatrany ny fitiavanao\nAmbaram-pialam-pofon''aiko Jesoa', '3');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF2', 'Fantatro fa sarotra ny diako\nMaro be ny zavatra ho sedraiko\nNefa raha Ianao no momba ahy\nDia tsy hanan-tahotra ny fo sy fanahy\nFiv:\nHanoa ny sitrakao, hanao ''zay tianao\nKa ho mpanompo vonona sy sahy koa\nHitory hatrany ny fitiavanao\nAmbaram-pialam-pofon''aiko Jesoa', '4');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF3','FF 3 : Jesoa Vato Fehizoro');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF3', 'Jesoa vato fehizoro\nAfa-tahotra ny fo\nRaha Ianao no mampijoro\nSy iankinana tokoa\nVato fiorenanay\nJesoa Zanaky ny Avo\nVato fialofanay\nAmin-dratsy te-handavo\nFiv:\nVato azo itokiana\nSady tsy mba mivena\nAfaka ny fahoriana\nRaha Ianao no ekena\nJesoa Tompo (irery) in-2\nNo ''lay vato tsy mifindra\nJesoa Tompo (irery) in-2\nNo ''lay fehizoro indrindra', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF3', 'Jesoa vato fehizoro\nMampisinda ahiahy\nFa mandresy horohoro\nManatanjaka fanahy\nO, ry vato tsara paika\n''Zay mandray Anao am-po\nDia mijoro tsy miraika\nN''inon''inona manjo\nFiv:\nVato azo itokiana\nSady tsy mba mivena\nAfaka ny fahoriana\nRaha Ianao no ekena\nJesoa Tompo (irery) in-2\nNo ''lay vato tsy mifindra\nJesoa Tompo (irery) in-2\nNo ''lay fehizoro indrindra', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF4','FF 4 : Ry Fanahy O, Midina');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF4', 'Ry Fanahy ô, midina\nManatreha anay anio Ianao\nMba hanoloranay ny vavakay mangina\nVetsovetsom-po ''zay atolotray\nVola sy ny harena samy tsy anananay\nFa indro vavaka mangina\nFanoloran-tena ho Anao tokoa.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF4', 'Ry Fanahy ô, monena\nAza mba mandao anay Ianao\nKolokoloy ''zahay mba hitombo fahendrena\nKa tsy hahafoy hiaraka Aminao\nOta sy rendrarendra\nSamy te-handavo anay\nSoa ihany, soa fa eo Ianao Fanahy\nVonona hitahy ny fiainanay', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF4', 'Ry Fanahy ô, meteza\nMba handray anay ho mpianatrao\nAvia Ianao, avia mampahereza\nHahatonga anahy mba ho irakao\nEso sy fitsapana\nSamy manakivy anay\nKa taomy, taomy hanam-po mafana\nHerim-po vaovao hiasa ho Anao', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF5','FF 5 : Izaho No Fananganana Ny Maty');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF5', 'Hoy Jesosy Tompo : ''Zaho no fananganana\nFananganana ny maty sy fiainana\n''Zay rehetra mino Ahy na dia maty aza\nDia ho velona indray (in-3)\nSatria velona Aho \nVelona ianareo\nAza matahotra fa\nHo velona indray (in-3)', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF5', 'Maharavo fo ny teniny, henoy indray koa\n''Zay rehetra velona ka mino Ahy tokoa\nDia tsy ho faty mandrakizay, hatsangako\nKa ho velona indray (in-3)\nSatria velona Aho \nVelona ianareo\nAza matahotra fa\nHo velona indray (in-3)', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF5', 'O, Jesosy Tompoko, indro re ampitomboy\nNy finoako Anao, ny herinao asehoy\nTompo iantrao ny foko, tsy finoako vonoy\nMba ho velona indray (in-3)\nMino Anao tokoa\nNy foko Jesoa\nFeno toky aho\nFa ho velona indray (in-3)', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF6','FF 6 : Ny Teninao');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF6', 'Ny teninao, Jehovah Ray mahery,\nIzay nomenao anay\nDia teny tsara sady mampahery\nKa mampifaly ny fonay\nFiv:\nNy teninao, mofon''aina, sakafo tokoa\nNy teninao, ranon''aina satria foto-tsoa\nNy teninao no teny tsy mba maty\nMaharitra mandrakizay.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF6', 'Soraty re ho velona ao anaty\nHitondra aina vaovao\nHitana ny finoana tsy ho faty\nKa hankato ny didinao.\nFiv:\nNy teninao, mofon''aina, sakafo tokoa\nNy teninao, ranon''aina satria foto-tsoa\nNy teninao no teny tsy mba maty\nMaharitra mandrakizay.', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF7','FF 7 : Tsy Hainay Ny Hangina');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF7', 'Tsy hainay ny hangina\nKa tsy hankalaza ny Avo\nNameno fitahiana\nKa tsy mba namela ho lavo\nFiv:\nKa dera ry Ray sy saotra\nNo atolotray ho Anao\nRaiso (fo sy fanahy) fo faly loatra\nNo entina midera Anao\nNy sainay tsy ampy hahalala\nNy fetran''ny fitiavanao\nKa raiso ry Tompo malala\nNy fankasitrahana Anao, ry Ray', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF7', 'Tsy hainay hadinoina\n''Lay fara-fitiavanao Ray ô,\nFa maty novonoina\nNy Zanakao hamonjy anay\nFiv:\nKa dera ry Ray sy saotra\nNo atolotray ho Anao\nRaiso (fo sy fanahy) fo faly loatra\nNo entina midera Anao\nNy sainay tsy ampy hahalala\nNy fetran''ny fitiavanao\nKa raiso ry Tompo malala\nNy fankasitrahana Anao, ry Ray', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF8','FF 8 : Masoko manganohano');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF8', 'Masoko manganohano,\nNitomany feno rano\nNoho ny helok’efa vita,\nDia mitony raha nahita\nMasonao be fiantra\nRy Mpamonjy ny mahantra\nO Voavotrao ‘lay very\nRy fitiavana mahery.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF8', 'Tanako mangadihady\nNisarangotra nitady\nFanavotana sy Vonjy\nF’efa resy ka nilonjy.\nDia noraisinao ry Tompo\nNa dia tànana maloto\nO voavotrao ‘lay very\nRy Fitiavana mahery.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF8', 'Foko tena fahavalo\nNefa resy ka mimalo\nRaha nifona fa naditra\nHitanao nipitrapitra.\nBabo ka tsy afaka ho aiza\n’Zay noraisinao ho sakaiza\nO voavotrao ‘lay very\nRy Fitiavana mahery.', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF9','FF 9 : Isaorana anie Jehovah');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF9', 'Isaorana anie Jehovah\nAndriamanitra anie\nAndriamanitry ny Israely\nIsaorana anie Jehovah\nAndriamanitra\nIzy irery ihany no nanao fahagagana\nIsaorana, mandrakizay\nny anarany Malaza\nAry aoka ny tany rehetra,\nHo henika ny famirapiratry\nNy voninahitr''i Jehovah Ray Tsitoha\nAmen!', '1');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF10','FF 10 : Mivavaka aho satria');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF10', 'Miloatra avy ao am-poko\nNy tonom-bavako\nManeho ny fanajako\nSy fankatoavako\nNy Tompoko Tsitoha\nNitahy hatrizay\nHatramin’ny taloha\nSy ho mandrakizay.\nFiv:\nMivavaka aho satria tena mino\nMino sy mankato\nManaja sady tia an’Andriamanitro\nMivavaka aho satria tena faly\nFaly sy manantena ny avotrao\nOmena mandrakizay doria', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF10', 'Mibahana ato am-poko\nFanahin’I Kristy\nMitaiza tsy miato\nKa mahafaly ery\nManesika ahy hivoatra\nHiasa bebe kokoa\nKa ataony tafahoatra\nNy hasoavako.\nFiv:\nMivavaka aho satria tena mino\nMino sy mankato\nManaja sady tia an’Andriamanitro\nMivavaka aho satria tena faly\nFaly sy manantena ny avotrao\nOmena mandrakizay doria', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF11','FF 11 : FALY AHO');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF11', 'Faly aho raha hoy izy ireo tamiko :\nHandeha isika ho any an-tranon''i Jehovah\nNy tongotray efa mijoro\nAo anatin''ny vavahadinao ry Jerosalema\nDia ianao ry Jerosalema, izay efa vita\nTahaka ny tanàna efa voalamina tsara\nAny no hiakaran''ny firenen''i Jehovah\nMba hisaotra ny anaran''i Jehovah.\n(H. RATSIMBAZAFY)', '1');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF12','FF 12 : AM-PELATANANAO RY RAIKO');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF12', 'Am-pelatananao ry Raiko\nNy momba ahy rehetra\nIriko Ianao no ho mpibaiko\nIanao anatiko ao mitoetra\nHo andrianiko tokoa\nHitarika ahy hatrany hatrany\nAndriafô tsy mandao Ianao\nJesoa Ianao no hazavana tsy ho lany\nAm-pelatananao ihany\nMandrakizay doria doria\nNa ny fiainako eto an-tany\nNa hatrarý ankoatra arý.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF12', 'Ny saiko am-pelatananao\nNy foko efa tsy ahy intsony\nFa voaova ho vaovao\nDia lasanao ry Ray ambony\nNy hasambarako ombieny\nMifaly manana anao\nIlay fiainako mpanjeninjeny\nNo voafehinao anatiko ao\nAm–pelatananao ihany\nMandrakizay doria doria\nNa ny fiainako eto an-tany\nNa hatrarý ankoatra arý.\n(ANDRIAMIHADAMAHATRARA M.)', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF13','FF 13 : MIHIRA FIHIRAM-BAOVAO');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF13', 'Mihirà fihiram-baovao\nHo an''i Jehovah ry tany\nMihirà fihirana ho an''i Jehovah\nDia ambarao amin''ny jentilisa\nNy voninahiny.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF13', 'Mihirà fihirana, fihiram-baovao\nMihirà fihirana ho an''i Jehovah ry tany\nDia ambarao amin''ny jentilisa\nNy voninahiny\nAmbarao amin''ny jentilisa\nFa Jehovah no mendrika\nIzy no mendrika ny fiderana.\n(RAMAROLAHY A.N.J.)', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF14','FF 14 : JESOA APETRAKA AMINAO');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF14', 'Jesoa, apetraka Aminao\nNy fanahiako rehetra\nNy asa vita hatrizay\nAnao tokoa tsy misy fetra\nJesoa apetraka aminao.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF14', 'Jesoa, apetraka Aminao\nNy asa fikelazan''aina\nTsy misy miafina aminao\nNy hetaheta tsy hay lazaina\nJesoa apetraka Aminao.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF14', 'Jesoa, apetraka Aminao\nNy toky sy fanantenana\nNy sitrakao no aoka hatao\nNa aiza lalana hombana\nJesoa apetraka Aminao.\n(RALAIARIJAONA)', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF15','FF 15 : RAIKO Ô !');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF15', 'Tompo malala tsitoha,inty aho\nIzao vao nandre ilay feo niantso ahy\nTsapako anatiko ao ny herinao\nMitaona ahy mba hiverina aminao.\n\nRaiko ô ! Raiko ô ! Inty aho\nRaiso mba ho isan''ireo zanakao\nFa izaho izay nania dia hiova\nHanaraka ny dianao Raiko ô !', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF15', 'Jesosy Zokiko ô ! Raiso aho\nHo isan''ireo zandrinao tianao\nNeniko ireny taloha,fa hiova\nRaiso aho ! Raiso aho Jesoa ! Ho anao.\n\nTompo ô ! Tompo ô ! Inty aho\nRaiso mba ho isan''ireo zandrinao\nFa izaho izay nanda dia hiova\nHanaraka ny dianao Jesoa.\n(J. RAMIANDRISOA)', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF16','FF 16 : Tsy vitam-po onena');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF16', 'Tsy vitam-po onena\nTsy vitan-dranomaso\nNy Asam-pamonjena\nTsy azo atao mifampiandry andraso\nTsy azo ifanerena\nMila fandavan-tena\nSy hery tsy mba maty\nHerim-po, herin-tsaina koa\n\nFiv : Na onena ny fonao\nNa tomany ny maso\nTsy izany no ilaina\nFa fifankatiavana', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF16', 'Ny mpianatry ny Tompo\nAsainy mifanompo\nNa mamy na mangidy\nMiara-dia , f’izany no Didy\nMamonjy ny mahantra\nManarina ny lavo\nMitsabo ny maratra\nAry koa tsy mba miavom-po\nNa onena ny fonao\nNa tomany ny maso\nTsy izany no ilaina\nFa fifankatiavana', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF17','FF 17 : HO AVY NY MARAINA');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF18','FF 18 : AFAFAZO');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF18', 'Raha tonga ny andro\nIzay hitsarana anao\nAmin''izay fotoana izay\nHadinina ianao\nKa hitarika ilay Mpanjakanao\nEfa vonona ve ianao ? (Ary koa) \nEfa ampy ve izay anananao\nHiatrehanao ny Tomponao.\nFiv:\nFihatsarambelatsihy\nAok''izay dieny ety\nFahafaham-baraka\nHo anao, ka ialao\nRaha toa tianao\nHo voavonjy ianao\nAfafazo ''zay voa mendrika\n(Mendrika).', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF18', 'Raha tonga ny andro\nIzay itsaràna anao\nAmin''izay fotoana izay\nHadinina ianao\nEfa ampy ve izay nafafinao\nMba ho voa mendrika tokoa ?(Ary ko) \nEfa tontosa ve ny anjaranao\nTsy hahameloka anao rahatrizay.\nFiv:\nFihatsarambelatsihy\nAok''izay dieny ety\nFahafaham-baraka\nHo anao, ka ialao\nRaha toa tianao\nHo voavonjy ianao\nAfafazo ''zay voa mendrika\n(Mendrika).', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF18', 'Raha ho avy ny fotoana hodianao\nAmim-boninahitra\nHo ao an-tranon-dray\nTsy hatahotra, na hangovitra\nIanao raha madio fo (Ary koa)\nSatroboninahitra tsy mba lo\nNo an''ny mpanomponao mahasoa.\n(RAZAFINTSEHENO M.)\nFiv:\nFihatsarambelatsihy\nAok''izay dieny ety\nFahafaham-baraka\nHo anao, ka ialao\nRaha toa tianao\nHo voavonjy ianao\nAfafazo ''zay voa mendrika\n(Mendrika).', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF19','FF 19 : RAHA JEHOVAH NO MPIANDRY');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF19', 'Raha Jehovah no Mpiandry,\nTsy mba hisy mampahory ;\nAhimaintso no handriana,\nNy fanahy velombolo ;\nEny, tsy hanahy akory\nLala-marina, lala-tsambatra\n(Sambatra ifaliana).\nFiv:\nRaha Jehovah no mpiandry\n(Tsy mba ... hisy)\nTsy mba hisy izay mampahory,\nAhimaintso no handriako\n(Ranon''aina ho sotroiko)\nNy fanahiko ho velominy\nMiasa Izy tsy mba matory,\nLala-marina itondrany\ntsy ho foiko.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF19', 'Ny aloky ny fasana aza\nTsy ho tazantsika intsony ;\nTsy hatahotra ny loza aho\n(Izy mihazona ahy)\nTsorak''azo nitondrany\nSy ny tehiny any ambony,\nNo manolotra ny toky\nNy fanahiko\n(Toky ny fanahiko).\nFiv:\nRaha Jehovah no mpiandry\n(Tsy mba ... hisy)\nTsy mba hisy izay mampahory,\nAhimaintso no handriako\n(Ranon''aina ho sotroiko)\nNy fanahiko ho velominy\nMiasa Izy tsy mba matory,\nLala-marina itondrany\ntsy ho foiko.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF19', 'Eo imasom-pahavalo\nNy latabatro velariny ;\nDiloilo feno hanitra\n(Efa namotosany ahy)\nSafononoka tantely\nNy kapoakako namboariny\nFahasoavana no sisa\nAfaka ahiahy\n(Afaka ahiahy).\nFiv:\nRaha Jehovah no mpiandry\n(Tsy mba ... hisy)\nTsy mba hisy izay mampahory,\nAhimaintso no handriako\n(Ranon''aina ho sotroiko)\nNy fanahiko ho velominy\nMiasa Izy tsy mba matory,\nLala-marina itondrany\ntsy ho foiko.', '3');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF19', 'Ao an-tranonao ry Tompo\nNo iriko hialofana ;\nKa tsy afa-miditra any\n(Ny hetraketraky ny tany)\nRaha ny fonao feno antra\nNo ho lamba irakofana,\nAry ny androm-pifaliana\nTsy ho lany (Tsy ho lany).\n(ANDRIAMANJATO R.\nRAJOELISOLO R.)\nFiv:\nRaha Jehovah no mpiandry\n(Tsy mba ... hisy)\nTsy mba hisy izay mampahory,\nAhimaintso no handriako\n(Ranon''aina ho sotroiko)\nNy fanahiko ho velominy\nMiasa Izy tsy mba matory,\nLala-marina itondrany\ntsy ho foiko.', '4');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF20','FF 20 : JESOA FITIAVANA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF20', 'Efa trotraky ny adin-tsaina\nVao mba nahatsiaro Anao ry Tompo\nMenatr''aho tsy sahy mitaraina\nRy Jesoa fitiavana\nKanjo tambitamby safosafo\nNo nasetrinao ny ditrako\nEny tsapako fa novidianao lafo\nTamin''ny ranao ny tenako.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF20', 'Jeso malalako tafio ny herinao\nHahaizako mandresy ny fankam-panahy\nHe! Tsinotsinona izao tontonlo izao\nFa fanimbana tena sy fanahy.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF20', 'Ho reharehako ilay tanambokovoko\nHihamina eo anilanao anie ny foko ;\nFa eo anilanao tsy misy ny tolokoloko,\nEo no mahatsara ahy, Jesoa.\n(ANDRIAMIADAMAHATRATRA M.)', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF21','FF 21 : MAHATOKY KA TOKONY EKENA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF21', 'Mahatoky ka tokony hekena\nAmin''ny fankasitrahana\nrehetra\nIzao teny izao (2)\nTonga teny ambonin''ny tany\nKristy Jesoa ;\nHamonjy ny mpanota,\nAry amin''ireny izaho\n(Izaho) 3\nAmin''ireny, izaho no lohany\nKanefa izao no namindrana fo\ntamiko (2)\nDia ny mba hisehoan''i\nJesoa Kristy eo amiko,\nizay lohany (2)\nNy fahari-po rehetra\nno fianarana\nHo an''izay hino Azy\nHahazoana fiainana\nMandrakizay ;\nMahatoky ..sns.\n', '1');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF22','FF 22 : TSY MENDRIKA AHY');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF22', 'Tsy mendrika ahy\nmpanomponao\nNy atao hoe zanakao\nKanefa Ianao ry Tompo\nNo niantso ahy izao\nIzay manatona ahy\nTsy ho laviko hoy Ianao\nDia tamy aho\nKa sahy nanatona Anao.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF22', 'Ianao no madio\n(mba diovy aho re)\nIanao no marina\n(mba hamarino aho re)\nIanao no masina \n(mba hamasino aho re)\nTsy mendrika hitsimpona\nNy latsaka amin''ny\nlatabatrao aho\nKanefa hoy Ianao\n"Izay manatona Ahy" \nNoho ny teninao (2)\nDia sahiko ny manatona.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF22', 'Ianao no madio\n(mba diovy aho re)\nIanao no marina \n(mba hamarino aho re)\nIanao no masina \n(mba hamasino aho re)\nMba diovy aho re,\nhamarino aho re\nMba diovy aho re\nMba hamarino re\n(hamarino re) \nMba hamasino re\n(hamasino re)\nMba diovy, hamarino,\nHamasino.\n(RABEMANAHAKA\nHenri RATSIMBAZAFY)', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF23','FF 23 : ANDRIAMANITRA O ! RY TOMPO ZANAHARY (A.T.Z)
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF23', 'Andriamanitra ô !\nRy Tompo Zanahary\nBe ny fitiavana\nMamonjy sy mamelona\nVatolampy, toky tsy mifindra\nFa maharitra mandrakizay.\nFiv:\nRaha eto an-tany\nDia mandala ihany\nMiovaova tsy azo\nIanteherana,\nFa izany no fomban''ny tany\nToa fiainana raha jerena\nKanjo indrisy fa mahonena\nHasambarana miserana ihany.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF23', 'O! ry velona tsarovy,\nny andronao fa mandalo\ntsy mba manam-pijanonana\nKa tandremo sao variana\nianao\n''Zao tontolo izao\nho levona avokoa.\nFiv:\nRaha eto an-tany\nDia mandala ihany\nMiovaova tsy azo\nIanteherana,\nFa izany no fomban''ny tany\nToa fiainana raha jerena\nKanjo indrisy fa mahonena\nHasambarana miserana ihany.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF23', 'Mbola ho entin''\nAndriamanitra hotsaraina\n''Zaho sy ianao, ka tsy ho\nafa-maina ;\nFa Jesoa Mpanjaka no hitsara\nKa hanavaka ny ratsy\nsy ny tsara.\n(RAMANOELINA\nANDRIANASOLO R.)\nFiv:\nRaha eto an-tany\nDia mandala ihany\nMiovaova tsy azo\nIanteherana,\nFa izany no fomban''ny tany\nToa fiainana raha jerena\nKanjo indrisy fa mahonena\nHasambarana miserana ihany.', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF24','FF 24 : MAHEREZA ! NY TOMPO MOMBA ANAO');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF24', 'Miainga, mazotoa\nMikeleza aina koa, \nmivavaha (2)\nHo an''izao tontolo izao\nMijoroa, mitoria\nMamafaza teny soa\nFa ny Tompo be fitia\nNo manohana anao\nSarotra ny lalanao\nKanefa matokia tokoa\nAndrandrao ny masonao\nFa mbola ao ny Tompo soa\nIzy no aminao\nKa hampandresy izao\nO ! mahereza\nfa ny Tompo momba anao.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF24', 'Mandrosoa, miasà\nTano ny Finoanao\nAza mba matahotra,\nna manahinahy izao\nMiadia, mitafia\nNy firaisan-tsaina soa,\nFa ny fahavalonao\nDia handositra tokoa\nAza kivikivy foana\nFa ny Tomponao dia ao\nMampahery lalandava\nAry koa tsy mba mandao\nIzy no aminao\nka hampandresy anao\nO ! mahereza\nfa ny Tompo momba anao.\n(RAMASITERA T.)', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF25','FF 25 : NY VOADIKO');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF25', 'Ny ondry mania no maneho\nAnao\n''Zao fanakena sy voady izao\nNandinika ny zava-bita Jesoa\nTsy mendrika hanatona Anao\nKanefa tsaroako ny\nfitiavanao\nHamonjy ity mpanomponao.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF25', '''Lay teny Mahery mananatra\nNampiova ny foko torovana\n''Lay Fanahy Masina inoako\nHampianatra sy hanadio\nNy ho aviko apetraka eo\nAminao\nIzany no voadiko izao.\n(RASOLOJAONA M.)', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF26','FF 26 : JESOSY REHAREHAKO');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF26', 'Tsy misy reharehako\nAfa tsy Ianao Jesosy o !\nAnao ny hery, anao ny haja\nsy voninahitra\nNy fasana sy ny fiainan-\ntsy hita\nIndro efa resinao\nIzao tontolo izao dia nahita\nFa efa nitsangana Ianao.\nFiv:\nSambatra aho Jeso ô ! \nManana Anao ''zay\nTompon''ny hery \nSambatra aho Jeso ô !\nMiankina eo an-tratranao\nAmpy ahy ny fitiavanao\nAmpy ahy ny finoako Anao\nRaketiko ao am-po\nlalandava\nFa reharehako Ianao.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF26', 'Tsy nisy nahitam-pamonjena\nAfa tsy taminao\nNanaiky Ianao\ntsy mba noterena\nHitondra aina vao\nNy tananao Mahery\nnatsotranao mba hitantana\nHahaizako mandeha \nsy mizotra ny lala-marinao.\nFiv:\nSambatra aho Jeso ô ! \nManana Anao ''zay\nTompon''ny hery \nSambatra aho Jeso ô !\nMiankina eo an-tratranao\nAmpy ahy ny fitiavanao\nAmpy ahy ny finoako Anao\nRaketiko ao am-po\nlalandava\nFa reharehako Ianao.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF26', 'Nasehonao ilay fahefana\nmanda sy aroko\nKa niala maina teo ''lay satana\n''zay fahavaloko\nTsy misy maharombaka\nakory\n''Zay eo an-tananao\nIzany indrindra no ilazako\nFa reharehako Ianao.\n(RAJAONARIVELO G.)\nFiv:\nSambatra aho Jeso ô ! \nManana Anao ''zay\nTompon''ny hery \nSambatra aho Jeso ô !\nMiankina eo an-tratranao\nAmpy ahy ny fitiavanao\nAmpy ahy ny finoako Anao\nRaketiko ao am-po\nlalandava\nFa reharehako Ianao.', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF27','FF 27 : ''LAY ALIM-PAMONJENA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF27', '''Lay alim-pamonjena (famonjena) \nTsarovana eto izao \nFa Tompo be harena \nno teraka tamin''io \nHe ! Mahagaga ilay fitia \nNasehon'' Andriamanitra\nFiv:\nHe ! Ravo ny fo \nHe!mirana koa \nMifaly (falifaly), sambatra doria \nMiventy ny hiran''ny lanitra \nHALELOIA !', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF27', '''Lay alim-pamonjena (famonjena) \nTsarovana eto izao \nFa vita fanekena \nIzaho sy ny Tompo soa \nNy tenako atolotro \nNy tenany ho avotro \nFiv:\nHe ! Ravo ny fo \nHe!mirana koa \nMifaly (falifaly), sambatra doria \nMiventy ny hiran''ny lanitra \nHALELOIA !', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF27', '''Lay alim-pamonjena \nTsarovana eto izao \nMpanota sesehena \nMandova fiainan-tsoa \n''Lay alim-pihavanana \nNitondra fiadanana \nFiv:\nHe ! Ravo ny fo \nHe!mirana koa \nMifaly (falifaly), sambatra doria \nMiventy ny hiran''ny lanitra \nHALELOIA !', '3');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF27', '''Lay alim-pamonjena \nTsarovana eto izao \nNitondra fietrena \nTsy mba manam-paharoa \nFitiava-mampitovy zo \nNy miadana sy ory fo\nFiv:\nHe ! Ravo ny fo \nHe!mirana koa \nMifaly (falifaly), sambatra doria \nMiventy ny hiran''ny lanitra \nHALELOIA !', '4');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF28','FF 28 : EFA VOADINIKAO ANDRIAMANITRA Ô');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF28', 'Efa voadinikao Andriamanitra ô! \nNy fiainanay zanak''olombelona \nTsy misy ''zay miafina amiko tsy hitanao \nIanao no manendry izany Andriamanitra ô!', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF28', 'Indro ny fikasako voalahatrao \nNy fiainako rehetra dia Aminao \nNy fahotana vitako taminao \nMangataka famelana Andriamanitra ô!', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF28', 'Aza avela aho hangaihay \nFa ampioreno mafy ny finoako \nKa na mamely ahy ny fakam-panahy \nDia mahatoky Anao ny fanahiko.', '3');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF28', 'Ny nofoko anie malemy no sady kely \nTorovana tsy ampy hery ka manahy \nMeteza Ianao anio Andriamanitra o! \nHo hery sy toky ho ahy mandrakizay.', '4');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF28', 'Ianao no vatolampy sy fiarovako \nNy herinao no indro hizorako \nNy foko ao anatiko ao efa fantatrao \nDia raiso aho ho zanakao Andriamanitra ô!', '5');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF29','FF 29 : TSY MBA HISY REHAREHAKO');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF29', 'Tsy mba hisy reharehako \nToy ny hazo nijalianao Jeso (Jesosy ô !) \nEo amin''izay rehetra halehako \nIzany no voadiko Aminao \nHe! izao rehetra izao ekeko (ry Jeso) \nMora lo sy tsy maharitra tokoa (kanefa Jeso) \nFa ny fitiavanao jereko \nRy Jeso tsy niala tamiko', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF29', 'Tsy mba hisy fanorenako \nRaha ny eto an-tany eto Raiko ô \nFa ny lanitra no ho fonenako \nIzany no mba faniriako \nHe! izao tontolo izao tazaniko (tazaniko) \nRendrarendr''izay mamitaka avokoa (kanefa Ray ô !) \nFa ny eo anilanao no mamiko ry Raiko \nRaiso aho ho Anao .', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF30','FF 30 : Fo mitambatra ho iray');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF30', 'Fo mitambatra ho iray\nNo entina eto izao\nMitrotro ny fitia\nHohamasinin’ Andriamanitra\nHitombo hery sy\nHamerovero koa\nKa hifanaiky ireo olon-droa\nFa hiombom-piainana\nNa inona miseho eto an-tany\nDia vonona hifampitantana\nFiv:\nFitiavana toy ny an’Ilay Jesoa\nFitiavana manolotra ny aina\nFitiavana mandresy fahotana\nFitiavana ho mandrakizay doria', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF30', 'Toy ‘zao androany izao\n‘Lay nofinofy izay\nNiainanay taloha\nKa indro fa miara-belona,\nMiara-tsambatra\nAnivon’ ny fitia\n‘Lay dina izay\nNiarahana nikolo\nNo hamafisina ho doria\nHotehirizina ho vaovao\nHandrobona, hitombo hasina\nFiv:\nFitiavana toy ny an’Ilay Jesoa\nFitiavana manolotra ny aina\nFitiavana mandresy fahotana\nFitiavana ho mandrakizay doria', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF31','FF 31 : MIFALIA RY MANAM-POFONAINA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF31', 'Mihirà ry manam-pofonaina\nMikaloa sy mitsikia\nTapaka ny gadra novonjena\nAfak''i Jesosy be fitia\n''Lay Edena nihidy tany aloha\n(nihidy aloha)\nNoho ny ota ratsin-\ndrazantsika\nHe, midanadana sy mivoha\nKa idiro hitondray mozika.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF31', '''Lay satana tena fahavalo\nResy teo ka voakodia\nKoa herezo aza mimalo\nTokinao anio ilay Mesia\nRy voahosotra hitondrana\nfamonjena\n(Ry voahosotra famonjena)\nNdreto ento ho Anao ''zahay\nDia izahay izay miferin''aina\nNoho ny ota izay mpahazo\nanay.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF31', 'Afaka izahay ankehitriny\nTonga ny Mpanavotra anay \nKa ilay gehenanay fahiny\nVoasolo paradisa indray\nKoa isaoranay tokoa tokoa\n(Koa isaoranay tokoa)\nIanao ry Rainay be fitia\nDia Ianao Jehovah Ray\nTsitoha\nNanome anay Ilay Mesia.\n(SOLOARIVELO R.\nSOLOARIVELO A)', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF32','FF 32 : AZA AVELA HAHALALA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF32', 'Aza avela hahalala\nNy antony nanaovanao\nNy hariva mamanala\nNa ireo maraina vao.\nFiv:\nTsy ho takatry ny saiko\nIzay rehetra alahatrao\nNy angatahako ry Raiko\nAoka aho hatoky Anao.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF32', 'Aza avela hanontany\nNy hafaliana atolotrao\nNa ny anton''ny tomany\nEfa haiko hatrizao.\nFiv:\nTsy ho takatry ny saiko\nIzay rehetra alahatrao\nNy angatahako ry Raiko\nAoka aho hatoky Anao.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF32', 'Aza avelanao hamefy\nNy ampitso izay Anao\nAza avelanao handrefy\nIzay ho fifalianao.\n(ANDRIAMANANTENA\nGeorges)\nFiv:\nTsy ho takatry ny saiko\nIzay rehetra alahatrao\nNy angatahako ry Raiko\nAoka aho hatoky Anao.', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF33','FF 33 : INTY AHO JESO');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF33', 'Inty aho Jesoa !\nMiantso ahy Hianao\nNa dia tsy mendrika,\ne manolo-tena izao\nIanao no nifidy,\ntsy haiko ny nandà\nTy antso sarobidy \nry Tompo, mamelà\nNy heloka rehetra,\ndiovy aho, havaozy\nHo olom-baovao\nhanao ny sitrakao\nHo mendrika tokoa\nny antsonao Jesoa.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF33', 'Inty aho Jesoa,\nmaintso ahy Hianao\nNy fahalemeko anie,\nry Tompo fantatrao\nHianao no nifidy\nsy maniraka ahy\nAvy aminao Jeso,\nny herin''ny fanahy\nHo entiko manoa\nny asa atolotrao\nFa sarotra e ny dia,\nry Tompo\nManampia,\nho mendrika tokoa\nNy antsonao Jesoa.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF33', 'Inty aho Jesoa,\nmiantso ahy Hianao\nMba ho fahazavan''izao\ntontolo izao\nHianao no nifidy ahy\nho fanasina\nHanefa ny adidy lehibe\nsy masina\nKoa hamasino Tompo\nFa vonona aho\nHanompo,\nhombay ny herinao\nHitondra aim-baovao\nHo mendrika tokoa\nNy antsonao Jesoa.\n(RATSIMBA M.\nNDRIAMAMPIANINA L)', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF34','FF 34 : TOMPO MALALA O !');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF34', 'Tompo malala o !\nMpanjakako Ianao\nTamy aho fa resy lahatra\nNoho ny famonjena vitanao\nEny ry Tompo,\ntsy ho haiko adinoina\nIntsony re ny fitiavana\nnasehonao.\nFiv:\nKa anio anavaozako\nny fanompoako anao\nFa tsy ho voavaliko\nizay natolotrao,\nNy fitiavana natolotrao\nKa raiso Tompo,\nekeo re sy anjakao\nNy fiainako ho voninahitrao.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF34', 'Tompo malala o !\n''Lay hazo fijalianao\nNiaretanao tao Kalvary\nNiaretanao Mpamonjiko\nNo nanehoanao fa tianao\nity mpanota izay\nTsy mendrika Anao akory ity.\nFiv:\nKa anio anavaozako\nny fanompoako anao\nFa tsy ho voavaliko\nizay natolotrao,\nNy fitiavana natolotrao\nKa raiso Tompo,\nekeo re sy anjakao\nNy fiainako ho voninahitrao.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF34', 'Tompo malala o !\ntsy ho hadinoiko intsony\nNy fitiavana natolotrao\n(Ny fitiavana natolotrao)\nEny ry Tompo, ity izaho\nizay voababonao\nFa vonona hanao ny sitrakao.\n(J.C. ANDRIAMANAN-\nTOANINA)\nFiv:\nKa anio anavaozako\nny fanompoako anao\nFa tsy ho voavaliko\nizay natolotrao,\nNy fitiavana natolotrao\nKa raiso Tompo,\nekeo re sy anjakao\nNy fiainako ho voninahitrao.', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF35','FF 35 : RY TOMPONAY O AVIA !');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF35', 'Ry Mpamonjiko\nRay malala o ! o ! o !\nMifona aminao ''ty Zanakao\nmitalaho Tompo o !\nFa diso taminao izahay\nka mangataka o ! o ! o !\nFamelana sy famindram-po\navy aminao Ray o !\nFiv:\nRy Tomponay ô ! avia,\nomeo anay izao\nAnio, ny fo madio\nsy masina anananao\nDia tsy ho very na hoe\nMbola ho diso ihany indray\nNy tena sy fanahy\nizay anananay.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF35', 'Ry Mpamonjiko\nRay malala o ! o ! o !\nBetsaka ireo ota efa vita\nsy nambabo anay\nKa angatahanay Aminao\nsy irinay koa o ! o !\nMba ho foana\ntsy hisy intsony re\nFa reraka izahay.\n(RANDRIANJAFIHARISON A.)\nFiv:\nRy Tomponay ô ! avia,\nomeo anay izao\nAnio, ny fo madio\nsy masina anananao\nDia tsy ho very na hoe\nMbola ho diso ihany indray\nNy tena sy fanahy\nizay anananay.', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF36','FF 36 : MIFALIA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF36', 'Vahoaka sesehena\nNo miara-paly izao\nMiventy ny Antema\nDia hira vaovao\nInjao fiderana\nManakoako ery\nManeho fanajana\nHo an''ilay Mesia.\nFiv:\nIza ary no tsy hifaly\n(Mifalia, mihobia)\nHisaotra ao am-po\n(Misaotra ao am-po)\nFa afa-doza, afa-jaly,\nafaka ianao\nHe, famonjena tokoa\nO! mihirà, mihobia\nafaka ianao\nTeraka ny Tompo tia,\nresy ny rafinao.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF36', 'Tsy ireo Anjely ihany\nNo faly ao am-po\nNy mpino eto an-tany\nMiravo avokoa\nFa indro ny Mpamonjy\nMpanjaka be fitia\nNietry an-tranon''omby\nHamonjy ny mania.\n(RAKOTOSON Pascal)\nFiv:\nIza ary no tsy hifaly\n(Mifalia, mihobia)\nHisaotra ao am-po\n(Misaotra ao am-po)\nFa afa-doza, afa-jaly,\nafaka ianao\nHe, famonjena tokoa\nO! mihirà, mihobia\nafaka ianao\nTeraka ny Tompo tia,\nresy ny rafinao.', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF37','FF 37 : Mifalia');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF37', 'Fa tsy misy fahoriana\n''Zay natolotrao anay\nKa nitarika hakiviana\nSatria efa zakanay\nNoho''lay vesatra indrindra Mafy,\nsy manonja koa\nlzay nentinao namindra\nRaha novonoina Ianao Jesoa !\nFiv:\nNy faniriam-panahy\nHiaraka Aminao\nHamelona ahy ho sahy\nHiasa ho Anao\nNy Famonjena soa\n''Zay nomenao ry Ray\nNo anton''ny fiainanay', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF37', 'Fa tsy misy hafaliana\nTena mampifaly anay\nRaha tsy ilay fitahiana\nAvy Aminao ry Ray\nFa tsy misy koa fitia\nHahasambatra anay\nRaha tsy Ianao ry Tompo tia\nNo mitoetra anatinay\nFiv:\nNy faniriam-panahy\nHiaraka Aminao\nHamelona ahy ho sahy\nHiasa ho Anao\nNy Famonjena soa\n''Zay nomenao ry Ray\nNo anton''ny fiainanay', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF37', 'Ny fisainanay ''zay kely\nTsy mahatakatr''indraindray\nFa Ianao, Ianao irery\nNo toky iankinanay\nNy finoana mila hivaha\nTsy mahasedra sarotra\nAmpijoroy tsy hanahy\nHo finoana matotra\nFiv:\nNy faniriam-panahy\nHiaraka Aminao\nHamelona ahy ho sahy\nHiasa ho Anao\nNy Famonjena soa\n''Zay nomenao ry Ray\nNo anton''ny fiainanay', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF38','FF 38 : MBA TODIHONAO NY LASA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF38', 'Mba todihonao ny lasa\nFa ho hitanao ny asa\nNandaniana ny fotoana\nFa dia vita mora foana.\nFiv:\nMitsangàna sahia ! sahia\nMijoroa, mitoria, mitoria\nAza kivy sanatria\nFa aminao Kristy.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF38', 'Eny tsara ho tsaroana\nIreo adidy nokoloina\nMaro koa no tsy tontosa\nNampitsotra ny kanosa\nFiv:\nMitsangàna sahia ! sahia\nMijoroa, mitoria, mitoria\nAza kivy sanatria\nFa aminao Kristy.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF38', 'Aoka hiezaka hatrany\nMba hanamarina izany\nFa hipetra ho tantara\nHo an''ny zanak''aman-para.\n(RAKOTOARITSIMBA\nGildas)\nFiv:\nMitsangàna sahia ! sahia\nMijoroa, mitoria, mitoria\nAza kivy sanatria\nFa aminao Kristy.', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF39','FF 39 : FANIRIAKO');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF39', 'Ray ô ! mba faniriako\nRaha toa ka sitrakao\nNy handova lanitra\nDieny ety an-tany\nKanefa tsy izaho irery\nF''ireo ''zay mino Anao\nHiaraka amiko hatrany\nhatrany.\nFiv:\nEndrey ny fiainana\nanilan-dRay, \nNy Tompo iray\nFanahy iray,\niraisana avokoa\n''Reo asa soa, iray ny fo\nIray ny loha, Ray ô !\nNahoana moa e !\nRaha atomboka anio ?', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF39', 'Ray ô ! mba faniriako\nRaha toa ka sitrakao\nNy ho zanaka manoa\nFeno fahendrena \nHilofo hatrany koa hamita\nIreo asanao\nKa tsy hirehareha\nFa hanetry tena.\nFiv:\nEndrey ny fiainana\nanilan-dRay, \nNy Tompo iray\nFanahy iray,\niraisana avokoa\n''Reo asa soa, iray ny fo\nIray ny loha, Ray ô !\nNahoana moa e !\nRaha atomboka anio ?', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF40','FF 40 : LA NUIT DU SALUT');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF40', 'C''était la nuit du salut\nOn s''en souvient à présent\nCar le seigneur très riche\nNaquit en ce moment-là\nC''est un amour fort étonnant\nQue Dieu a bel et bien\nmontré\nFiv:\nLe cœur est rempli\nd'' immense gaieté\nDe joie, du bonheur éternel\nOn chante tout haut\nla chanson du Ciel\nHaleluia !', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF40', 'C''était la nuit du salut\nOn s''en souvient à présent\nJ''ai conclu avec le Seigneur\nLe pacte de la rédemption\nÀ lui j''ai donné tout\nmon corps\nPour moi, son sang est\nmon rachat.\nFiv:\nLe cœur est rempli\nd'' immense gaieté\nDe joie, du bonheur éternel\nOn chante tout haut\nla chanson du Ciel\nHaleluia !', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF40', 'C''était la nuit du salut\nOn s''en souvient à présent\nDes pécheurs innombrables\nHéritent la belle vie\nCette nuit réconciliant\nÉtait porteuse de la paix.\nFiv:\nLe cœur est rempli\nd'' immense gaieté\nDe joie, du bonheur éternel\nOn chante tout haut\nla chanson du Ciel\nHaleluia !', '3');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF40', 'C''était la nuit du salut\nOn s''en souvient à présent\nMon Jésus s''est rabaissé\nPar sa suprême pitié\nO ! bel amour rendant égaux\nLes riches et les malheureux.\n(RAJOELISON)\nFiv:\nLe cœur est rempli\nd'' immense gaieté\nDe joie, du bonheur éternel\nOn chante tout haut\nla chanson du Ciel\nHaleluia !', '4');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF41','FF 41 : JESOA NO MPANAVOTRA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF41', 'O ! iza iza ?\nIza no hamonjy ahy\nIza, iza no hamonjy ahy re ?\nFa rerak''aho e !\nFa dia reraka\nMilonjy entam-pahotana\nFahotana be.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF41', 'O ! manatona\nManatona an''i Jesoa\nFa izy ihany \nIzy no hamonjy anao.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF41', 'Fa noreseny\nEfa noreseny na ny\nFasan''aza dia ny\nFahafatesana !\nNy fasan''aza !\nEfa noreseny izao\nNy fahafatesana\nIlay fahavalonao\nKa mba mifalia !\nMifalia ianao\nFa Jesoa Tompo no\nMpanavotra anao.', '3');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF41', 'Ry Jesoa Tompo !\nRy Jesoa Tompo ô !\nMba raiso re ny foko\nHo anao izao !\nFa famonjena\nFanavotan-dehibe\nSy avotr''aina koa\nNo he atolotrao.', '4');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF41', 'Fa novonoina !\nNovonoina Ianao\nHisolo voina\nMba hisolo voina anay.', '5');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF41', 'Fa noresenao\nEfa noresenao na ny\nFasan''aza dia \nNy fahafatesana !\nNy fasan''aza !\nEfa noreseny izao\nNy fahafatesana\nIlay fahavalonao\nKa mifalia re !\nKa mifaly izahay\nFa Jesoa Tompo no\nMpanavotra anay.\n(BESSA)', '6');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF42','FF 42 : MOA MISY VA ?');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF42', 'Moa misy va ety,\niriko toa Anao ?\nRy Tompo be fitia,\nMpanao izao rehetra izao\nNy foko lazanao\nry Ray malala ô\nJereo ''ty Zanakao\nmiantoraka Aminao.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF42', 'Moa misy va ety,\niriko toa Anao ?\nJesoa Sakaiza, avia Hianao\nhiara-dia izao\nNy foko tia Anao,\noneno, anjakao\nTsy hatakaloko Hianao,\n"Ry fiainako vaovao"', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF42', 'Moa misy va ety,\niriko toa Anao ?\nFanahy hendry ery,\nMpanoro izay rehetra atao\nNy foko he ! Anao,\nFanahy Masina ô !\nNy fiainako ovay ho vao,\nka hamasinonao.', '3');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF42', 'Moa misy va ety,\niriko noho ilay\nNesoina tany Kalvary ? \n''lay indrafon''ny Ray\nJeso malala ô !\nEndrey ''ty vitanao\nHitanao ny hetahetako\nsy hasambarako.\n(RAKOTONIRAINY J.)', '4');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF43','FF 43 : AIZA MOA IANAO ?');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF43', 'Jesosy Tompo miantso anao\nManontany izao\n"Aiza moa ianao ?"\nNy ondrikelinao noana indreo\nMiandry anao re ireo\n"Aiza moa ianao ?"\nFiv:\n''Ty aho Jeso, ekeo \n(ekeo mba) ho irakao\nRaiso hanao ny sitrakao\ntokoa\nJesosy ô ! (omeko)\nmba ho Anao\nNy foko, tenako,\nny fanahiko.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF43', 'E ! lalan-tsarotra ilàna anao\nAdy mafy no ao,\n"Aiza moa ianao ?"\nReseonao\n''zao tontolo izao\nMba ho ahy re izao,\n"Aiza moa ianao ?"\nFiv:\n''Ty aho Jeso, ekeo \n(ekeo mba) ho irakao\nRaiso hanao ny sitrakao\ntokoa\nJesosy ô ! (omeko)\nmba ho Anao\nNy foko, tenako,\nny fanahiko.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF43', 'Ny hery, aina sy fanananao\nNo ilaiko izao\n"Aiza moa ianao ?" \nNy teninao sy ny fanahinao\nNo takiako izao\n"Aiza moa ianao ?"\nNy sandriko no hiaro anao\nHisakambina anao\n"Aiza moa ianao ?"\nRaha ny Fanahy \nno hitantana anao\nVonona ve ianao ?\n"Aiza moa ianao ?"\n(HERITSIMBA A.R.)\nFiv:\n''Ty aho Jeso, ekeo \n(ekeo mba) ho irakao\nRaiso hanao ny sitrakao\ntokoa\nJesosy ô ! (omeko)\nmba ho Anao\nNy foko, tenako,\nny fanahiko.', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF44','FF 44 : RY MADAGASIKARA SOA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF44', 'Ry Madagasikara soa\nNatolo-janahary anay\nNosim-boahangy manirery\nFa nahasoa ny razanay\nHe ! sarobidy tsara loatra\nTsy sandana, tsy atakalo\nFeno ny fo ka toa mangina\nHeriny mivavaka hoe :\nAndriamanitra ô !\nTondrahy andon''ny lanitra\nVontosy ny tsiron''ny tany\nI Madagasikara.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF44', 'Ry firenena Malagasy\nAntsoin''ny Nosy hianao\nAndeha malaky, mitsangàna\nAlao hery, mandrosoa !\n''Zay mahaolona anao re\nAtolory, asorony\nAza mandevina talenta\nFa anondroty Firenena e !\nAndriamanitra ô !\nTondrahy andon''ny lanitra\nVontosy ny tsiron''ny tany\nI Madagasikara.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF44', 'Andriamanitra ambony\nFenoy ny fiahianao\nNy mponina rehetra eto\nAry izay aza mahasoa\nIreo nantsoinao hitondra\nMba tsidiho, mba tariho\nHo sambatra tokoa ny Nosy\nRaha Hianao no azy e !\nAndriamanitra ô !\nTondrahy andon''ny lanitra\nVontosy ny tsiron''ny tany\nI Madagasikara.\n(RAKOTONJANAHARY A.)', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF45','FF 45 : HIRA FIDERANA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF45', 'Hira fiderana ho anao ry Ray\nTsy afaka ato am-bavako\nmandrakizay\nHira mampifaly, \nmampitony jaly\nMampiravoravo fo.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF45', 'Ny talenta izay efa nomenao\nahy\nHiderako Anao\nn''aiza misy ahy\nMba hamelon''aina,\nhampitony saina\nHampiray ny miaina.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF45', 'Hira tsotra ihany,\nnefa na dia izany\nHo Anao tokoa\nno hikaloako\nRaiso re, Tompo,\nF''io no porofom-pitiavako\nAnao.', '3');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF45', 'Hira avy ato am-poko,\nindro fa vetsoiko\nHo Anao ry Raiko\nizay antsoiko\nHira tena mamy,\nmanambara hatrany\nFa tena tiako Ianao.\n(M. RAKOTOMAMONJY)', '4');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF46','FF 46 : MBA TANTANONAO, RY RAIKO !');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF46', 'Mba tantanonao ry Raiko\nIzato lalan-janakao\nNy mifidy toa tsy haiko\nFa maniry indrindra Anao\nHitanao ny adi-tsaina\nMitambesatra ao an-doha\n''Zany tsikin''ny maraina\nToa ela vao hifoha.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF46', 'Moa diso ny safidiko\nRaha mandroso ka mankao\nSa ''ty lalà-nofinidiko\nDia tsy ilay voasoritrao\nTanteraho ny faniriako\nO ! ry Tompo tena tia\nFa ity zavatra iriko\nMasina amiko doria.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF46', 'Nefa raha tsy izay no didy\nNamboarinao ho ahy\nAmboary ny safidy\nSy ny ratrako am-panahy\nAtolorinao ny hery\nHampijoro izay nikoa\nKa sitranonao ny fery\nSy ny ratrako ao am-po.\n(G. ANDRIAMANANTENA)', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF47','FF 47 : FIADANANA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF47', 'Ianao ''zay mitady\nny fiadanantsoa\nAmin'' izao fiainana izao\nKa mandeha mankatsy\narý sy aroa\nMikorana ary koa milalao\nKanefa tsy mahita\nizay mahasoa\nFa vizana foana ny nofo\nInty misy fiainana\natoroko anao\nAndramo dia ho hitanao.\nFiv:\nJesosy, Jesosy\n(Jesosy Tompo\nsy Mpamonjy tia)\nNo fiainana atoroko\nanao (Jeso)\nJesosy, Jesosy\n(Jesosy Tompo\nsy Mpamonjy tia)\nNo hany hampiadana anao\nKoa raiso Jesosy\nHo Tompo Mpamonjy\nHanapaka ny fiainanao\nDia ho hitanao ''lay fiadanana\nHaharitra mandrakizay.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF47', 'Ianao ianjadiam-pahoria-\nmarobe\nAmin''izao fiainana izao\nTsy misy hazavana \nfa toa haizim-be\nNo mandrakotra\nny fiainanao\nKa mila ho kivy\nsy hamoy fo\nNoho ireo adim-piainana maro\nInty misy fiainana\natoroko anao\nAndramo dia ho hitanao.\n(ANDRIANAIVONIAINA N.)\nFiv:\nJesosy, Jesosy\n(Jesosy Tompo\nsy Mpamonjy tia)\nNo fiainana atoroko\nanao (Jeso)\nJesosy, Jesosy\n(Jesosy Tompo\nsy Mpamonjy tia)\nNo hany hampiadana anao\nKoa raiso Jesosy\nHo Tompo Mpamonjy\nHanapaka ny fiainanao\nDia ho hitanao ''lay fiadanana\nHaharitra mandrakizay.', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF48','FF 48 : ANDRIAMANITRA FITIAVANA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF48', 'Andriamanitra Fitiavana Ianao\nKa mihevitra ny momba\nanay etý\nRaha mifoha maraina\nizahay\nDia tsy afaka am-bavanay\nhoe: Ampy ho anay\nny fahasoavanao\nFiv:\nRaha maraina mibaliaka\nny andronay etý\nIanao no masoandro\nniaviany\nRaha ny alin''ny ririnina\nno handalo etý\nIanao no kintana\nhitantana ny dia\nKa ny vavaka atao\nIzay mipololotra\nam-ponay ao hoe :\nIzahay anie hatoky Anao.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF48', 'Andriamanitra Fitiavana Ianao\nKa mitantana ny dianay etý\nRaha mandimby ny andro\nny alina\nMbola atokana hideranay\nhoe: Ampy ho anay\nny fahasoavanao.\n(Ndriana RAMAMONJY)\nFiv:\nRaha maraina mibaliaka\nny andronay etý\nIanao no masoandro\nniaviany\nRaha ny alin''ny ririnina\nno handalo etý\nIanao no kintana\nhitantana ny dia\nKa ny vavaka atao\nIzay mipololotra\nam-ponay ao hoe :\nIzahay anie hatoky Anao.', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF49','FF 49 : MIFONA RE RAY !');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF49', 'Mifona re ry Ray\nIreto Zanakao\nFa diso izahay\nTsy mety ny natao.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF49', 'Ny ota sesilany\nNandramana avokoa\nKanefa re izahay\nIfonana tokoa.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF49', 'Izay rehetra hita\nNatao fa tsy ho to\nIzao ny ratsy vita\nNandrotika ny fo.', '3');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF49', 'Ry Jeso ô ! anio\nIndreto izahay\nManaiky ka fidio\nHo zanakao ry Ray.', '4');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF49', 'Tsy tsapanay tokoa\nNy antranao ry Ray\nFa hay, ny zava-tsoa\nDia takona aminay !', '5');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF49', 'Maniry fahafahana\nIzao ''ty fo madio\nEkeo ny fibebahana\nAtolotray anio.\n(RAKOTOMALALA J.)', '6');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF50','FF 50 : IANAO IZAY MIASA FATRATRA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF50', 'Ianao izay misa fatratra\nKa mikendry mba hanefa\nzava-tsarotra\nMoa tsy tsapanao ho\nmahasambatra\nNy asa izay atao\nam-pitiavana !\nMihamaivana mora entina\nNy fahasahiranana toa\nmitambesatra\nRaha ao ny Manaiky\nmarina\nHo tia sy handala\nny asa rehetra.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF50', 'Hoy ny hafatry ny Tompo\naminao\nManatona ahy raha toa\nsahirana\nFa izaho tsy manary \nna mandao\nIzay miezaka \nka hita reraka\nNefa moa ve, vonon''izao\nHandà ny tenanao,\nhamoy izao tontolo izao\nDia manaiky koa mba\nho tarihina\nFa sarotra ny làlana\nsy asa atao.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF50', 'Mitsanganà,\nmandrosoa hiasa e !\nFa malalaka ny saha\nizay omena anao\nDia meteza koa hanaiky\nhanome\nHarena herim-po\nilaina aminao\nAza ketraka,\nmatokia izao\n''Lay hery ''zay manohana\nNy fihezahanao\nKa mitondra soa,\nmanova mba ho vao\nNy vokatry ny asa\nizay nanasatr''anao.\n(RANDRIANANDRAINA S.)', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF51','FF 51 : RY MPAMONJY OLOM-BERY');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF51', 'Ry Mpamonjy olom-bery\nSarobidy sy mahery\nNy fitiavanao ahy\n''Zay mambabo ny fanahy\nNoho ilay fitiavanao\nBabonao ny foko izao\nKa izao rehetra izao\nTsy hamoizako Anao.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF51', 'Ny fitiavanao, ry Tompo\nNo mambabo ahy hanompo\nTia ahy Hianao\nKa mba tiako tsotra izao\nEny, fantatrao fa tiako\nIzao fiainana eto izao\nTiako izao rehetra izao\nNefa mbola tiako Hianao.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF51', 'Na dia sarotra sy mafy\nNy hanaraka Anao\nIzaho vonona hihafy\nF''efa tiako Hianao\n''Zao fiainana izao\nTsy atakaloko Anao\nNa dia mamiko aza eto\nMbola tiako Hianao.\n( RAJAONAHSON J.P)', '3');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF52','FF 52 : AVIA ! TORIO KRISTY');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF52', '\nFiv:\nRy tanora ô ! avia !\nMitsangàna, mitoria\nAmbarao ''lay famonjena\nka sahia\nAvia, avia, torio Kristy.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF52', 'Fa vory amintsika izao ny hery\nKa mendrika hanaovan-tsoa\nMeteza ka ampio ''reo\nmbola very\nHo mpino toa antsika koa.\nFiv:\nRy tanora ô ! avia !\nMitsangàna, mitoria\nAmbarao ''lay famonjena\nka sahia\nAvia, avia, torio Kristy.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF52', 'Anio miandry antsika\nny tontolo\nHitondra hazavana soa\nIsika izay voataiza \nsy voakolo\nNo indro irahan''i Jesoa.\nFiv:\nRy tanora ô ! avia !\nMitsangàna, mitoria\nAmbarao ''lay famonjena\nka sahia\nAvia, avia, torio Kristy.', '3');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF52', 'Ny herin''ny fanahy\nno momba antsika\nHahatomombana ny dia\nAndeha amin''ny endrika\nsariaka\nNo hitoriana an''i Kristy.\n(RANDRIANDRAINY F.)\nFiv:\nRy tanora ô ! avia !\nMitsangàna, mitoria\nAmbarao ''lay famonjena\nka sahia\nAvia, avia, torio Kristy.', '4');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF53','FF 53 : HAVAOZIKO ILAY VOADY');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF53', 'Tsy ho toy izao,\nity mpanomponao\nRaha toa tsy tao tokoa\nny fahasoavanao\n''Zay nanohana \n''lay hery kely\ntao am-poko\nNahazoako mba nanao\nasam-panompoako.\nFiv:\nIndro fa havaoziko,\nry Jesoa\n''Lay voady\nmiorin''ao am-po\nNy andro ivelomako\n''Zay omenao iainako\nDia atokako sy mbola\nhitoriako\nIlay fitiavanao !\nNy asa izay\nnataon''i Jesoa\nDia tsapako ao am-po\nFa tsy dia toy inona\nsy bitika tokoa\nNefa na izany aza\ndia inoako koa\nFa ''zay kely vitako :\nhitera-bokatsoa.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF53', 'Na ho lava na ho fohy\nny androko ety\n''Zaho dia irakao,\nry Jesoa Tompo be fitia\nKa ilay fitiavanao\nmiorim-paka anaty\nHo hany herim-pitokiana\nmandra-maty.\n(D. RANOASON)\nFiv:\nIndro fa havaoziko,\nry Jesoa\n''Lay voady\nmiorin''ao am-po\nNy andro ivelomako\n''Zay omenao iainako\nDia atokako sy mbola\nhitoriako\nIlay fitiavanao !\nNy asa izay\nnataon''i Jesoa\nDia tsapako ao am-po\nFa tsy dia toy inona\nsy bitika tokoa\nNefa na izany aza\ndia inoako koa\nFa ''zay kely vitako :\nhitera-bokatsoa.', '2');
INSERT INTO song (author_id, category_id, reference, title) VALUES (1,1,'FF54','FF 54 : NY FINOANA, NY FANANTENANA, NY FITIAVANA');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF54', 'Mandrosoa hatrany\nAza kivy foana\nZava-tsoa tsy lany ao anaty\nNy finoana.\nFiv:\n''Reto no miray (indray)\nHery lehibe tokoa\n(tokoa)\nDia ny finoana\nNy fanantenana\nNy fitiavana.', '1');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF54', 'Tano tsy ho very\nNy fanantenana\nFa mitondra hery\nEny an-dàlana ombana.\nFiv:\n''Reto no miray (indray)\nHery lehibe tokoa\n(tokoa)\nDia ny finoana\nNy fanantenana\nNy fitiavana.', '2');
INSERT INTO verses (song_reference,lyrics, reference) VALUES ('FF54', 'Aina vaovao\nHerin''ny fanahy\nNy fitiavanao\nKa tanako tokoa ho ahy.\n(RAMAROLAHY Marka\nRAMAROLAHY Lioka)\nFiv:\n''Reto no miray (indray)\nHery lehibe tokoa\n(tokoa)\nDia ny finoana\nNy fanantenana\nNy fitiavana.', '3');
